class Paragraph extends BaseParagraph
{

    public function getPathPhotoRu()
    {
        return ($this->getPhotoRu() !== null) ? sfConfig::get('app_articles_photo_dir') . '/ru/' . $this->getPhotoRu(
        ) : false;
    }

    public function getPathPhotoEn()
    {
        return ($this->getPhotoEn() !== null) ? sfConfig::get('app_articles_photo_dir') . '/en/' . $this->getPhotoEn(
        ) : false;
    }

    public function getParagraphStyle()
    {
        $res = ' class="txt';
        $out = array();
        if ($this->getIsBold()) {
            $res .= ' bold';
        }
        if ($this->getIsItalic()) {
            $res .=  ' ita';
        }
        if ($this->getHStyle() != 'none') {
            $res .= ' h' . $this->getHStyle();
        }
        $res .= '"';

        if($this->getPadLeft() > 0){
            $res .= ' style="padding-left: '.$this->getPadLeft().'%"';
        }

        return $res;
    }

    public function getParagraphEnBr()
    {
        return nl2br($this->getParagraphEn());
    }

    public function getParagraphRuBr()
    {
        return nl2br($this->getParagraphRu());
    }
}
